import { Address, Cell, Contract, ContractProvider } from 'ton-core'

// snippet from: https://github.com/ton-community/sandbox/blob/main/examples/contracts/NftItem.ts

export interface NftItemData {
    inited: boolean
    index: number
    collection: Address | null
    owner: Address | null
    content: Cell | null
}

export class NftItem implements Contract {
    static readonly code = Cell.fromBase64('')

    constructor(readonly address: Address, readonly init?: { code: Cell; data: Cell }) {}

    static createFromAddress(address: Address) {
        return new NftItem(address)
    }

    async getData(provider: ContractProvider): Promise<NftItemData> {
        const { stack } = await provider.get('get_nft_data', [])
        return {
            inited: stack.readBoolean(),
            index: stack.readNumber(),
            collection: stack.readAddressOpt(),
            owner: stack.readAddressOpt(),
            content: stack.readCellOpt(),
        }
    }
}
